$("head").append("<style>\n.btn-controls {height: 35px;padding: 9.5px;cursor: pointer;border-radius: 4px;border: 1.5px solid black;background-color: white;}.btn-controls-no-active-toggle{height:35px;padding:9.5px;cursor:pointer;border-radius:4px;border:1.5px solid #000;background-color:#fff}.a-btn,.btn-controls-no-active-toggle:active{border:1.5px solid green}.inputarea{box-sizing:border-box;overflow:auto;border:1px solid #000;border-width:0 1px 1px;width:100%;height:200px;outline:transparent solid 0;padding:8.5px;border-radius:0 0 4px 4px}.btn-nav-controls{border:1px solid #000;padding:6.5px;border-top-left-radius:4px;border-top-right-radius:4px;user-select:none;background-color:#eee}</style>");$("ctreax").empty();$("ctreax").html('<div class="btn-nav-controls"><button class="btn-controls bold"><i class="bi bi-type-bold"></i></button> <button class="btn-controls italic"><i class="bi bi-type-italic"></i></button> <button class="btn-controls underline"><i class="bi bi-type-underline"></i></button> <button class="btn-controls-no-active-toggle links"><i class="bi bi-link"></i></button></div><div class="inputarea" contenteditable></div></div>');$(".inputarea").focus().blur().keydown(function(a){if(13==a.keyCode)return document.execCommand("insertHTML",!1,"<br><br>"),!1});$(".btn-controls").on("click",function(){document.execCommand($(this).attr("class").split(" ")[1],!1);this.classList.contains("a-btn")?this.classList.remove("a-btn"):this.classList.add("a-btn");$(".inputarea").focus()});$(".links").on("click",function(){linkURL=prompt("Enter a URL:");if(!linkURL)return!1;document.execCommand("insertHTML",!1,'<a contenteditable="false" href="'+linkURL+'" target="_blank">'+linkURL+"</a>")});$(".inputarea").on("paste",function(a){a.preventDefault();$(this).append(window.event.clipboardData.getData("text/plain"))});
